dynaForm.JS:


PROCESS_REQUEST_FILE = '../elock/elock_Ajax';

function editUser(USR_USERNAME)
{


	alert(USR_USERNAME);
    	var uri = 'request=editUser&USR_USERNAME='+USR_USERNAME;
        alert(uri);
    	popupWindow('', '../elock/elock_Ajax?'+uri, 355, 245);

}


function updateUser(USR_USERNAME) {

  	username = $('form[USER_NAME]').value;
	password = $('form[PASSWORD]').value;
	if(password == '') {
		new leimnud.module.app.alert().make({label: "Please Enter a valid Password"});
		return false;
	}

  	try{
	parent = $('form[ROL_PARENT]').value;
	}catch(e){}

	status = $('form[ROL_STATUS]').value;

    var uri = 'request=updateUser&username='+username+'&parent='+parent+'&password='+password;

  	var oRPC = new leimnud.module.rpc.xmlhttp({
  		url : PROCESS_REQUEST_FILE,
  		args: uri
  	});

  	oRPC.callback = function(rpc){
	  	var oRPC = new leimnud.module.rpc.xmlhttp({
		    url   : PROCESS_REQUEST_FILE,
		    async : false,
		    method: 'POST',
		    args  : 'request=show'
	  	});
		oRPC.make();
		$('publisherContent[0]').innerHTML = oRPC.xmlhttp.responseText;
		currentPopupWindow.remove();
	}.extend(this);
	oRPC.make();
};
