dynaForm._JS:


	var dynaformOnload = function(){
	document.getElementById("form[DEMO_MODE][On]").onclick = function(){
	switchMode(document.getElementById("form[DEMO_MODE][On]"));

	}
	document.getElementById("form[DEMO_MODE][Off]").onclick = function(){
    switchMode(document.getElementById("form[DEMO_MODE][Off]"));

    }

    if(document.getElementById("form[DEMO_MODE][On]").checked) switchMode(document.getElementById("form[DEMO_MODE][On]"));
    if(document.getElementById("form[DEMO_MODE][Off]").checked) switchMode(document.getElementById("form[DEMO_MODE][Off]"));
  }






	function switchMode(fieldCheck){

	//alert(fieldCheck.checked);
	if(fieldCheck.value=="On"){
	enableDemoMode();
	}else{
	disableDemoMode();
	}
	}
	var fieldsNames=new Array("ElockWebService_WSDL","ElockOperatorUserName","ElockOperatorUserPassword","ElockAPP_PASSWORD","ElockFTP_PATH","ElockFTP_FOLDER","ElockSERVER_IP","ElockFTP_USER","ElockFTP_PASSWORD","ElockSigner_USER");
    var labelsNames=new Array("SUBTITLE_WS","ElockOperatorTitle","ElockFTP_Credentials");
	function enableDemoMode(){

	//Empty Fields
	for(i=0;i<fieldsNames.length;i++){
	if(getField(fieldsNames[i])){
	getField(fieldsNames[i]).value="";
	//Hide Fields
    hideRowById(fieldsNames[i]);
	}
	}
	for(i=0;i<labelsNames.length;i++){
    //Hide Fields
    hideRowById(labelsNames[i]);
    }


	}
	function disableDemoMode(){
	//Show Fields
	//Empty Fields
    for(i_fields=0;i_fields<labelsNames.length;i_fields++){
    //Unhide Fields
    //alert(labelsNames[i_fields]);
    showRowById(labelsNames[i_fields]);
    }
    for(i_fields=0;i_fields<fieldsNames.length;i_fields++){
    //alert(i_fields);
    //alert(fieldsNames[i_fields]);
    showRowById(fieldsNames[i_fields]);

    }

	}
